mapper.insert:
INSERT INTO noepa.noepa_base_data
( time
, delta
, theta
, low_alpha
, high_alpha
, low_beta
, high_beta
, low_gamma
, mid_gamma
, attention
, meditation)
VALUES ( #{time}
       , #{delta}
       , #{theta}
       , #{low_alpha}
       , #{high_alpha}
       , #{low_beta}
       , #{high_beta}
       , #{low_gamma}
       , #{mid_gamma}
       , #{attention}
       , #{meditation});

INSERT INTO noepa.hackathon_member
( mb_id
, mb_pw
, mb_name)
VALUES ( #{mb_id}
       , #{mb_pw}
       , #{mb_name});

mapper.select:
select *
from noepa.hackathon_member
where mb_id = #{mb_id}
AND mb_pw = #{mb_pw}

mapper.insert:
INSERT INTO noepa.hackathon_qna
( qna_writer
, qna_title
, qna_contents)
VALUES ( #{qna_writer}
       , #{qna_title}
       , #{qna_contents});

INSERT INTO noepa.hackathon_comments
( qna_id
, comments_writer
, comments)
VALUES ( #{qna_id}
       , #{comments_writer}
       , #{comments});
